cur_frm.cscript.net_weight = function(doc, cdt, cdn) {
 msgprint("Please add UOM conversion details:\n UOM: "+ doc.weight_uom
+"\n Conversion Factor: " +(1/doc.net_weight));
}

cur_frm.add_fetch('customer','credit_days','credit_days')
cur_frm.add_fetch('item_code','net_weight','net_weight');
cur_frm.add_fetch('item_code','weight_uom','weight_uom');
cur_frm.cscript.quantity_in_weight = function(doc, cdt, cdn) {
    var item = frappe.get_doc(cdt, cdn);
    item.qty = item.quantity_in_weight / item.net_weight;
    refresh_field("qty", item.name, item.parentfield);
};
cur_frm.cscript.rate_per_weight_unit = function(doc, cdt, cdn) {
    var item = frappe.get_doc(cdt, cdn);
    item.rate = item.rate_per_weight_unit * item.quantity_in_weight / item.qty;
    refresh_field("rate", item.name, item.parentfield);
},
cur_frm.cscript.posting_date = function(doc){
        cur_frm.set_value("due_date", frappe.datetime.add_days(doc.posting_date,doc.credit_days));
}

