column8:
{%- macro add_header(page_num, max_pages, doc, letter_head, no_letterhead) -%}
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
    {%- if doc.meta.is_submittable and doc.docstatus==0-%}
    <div class="alert alert-info text-center">
        <h4 style="margin: 0px;">{{ _("DRAFT") }}</h4></div>
    {%- endif -%}
    {%- if doc.meta.is_submittable and doc.docstatus==2-%}
    <div class="alert alert-danger text-center">
        <h4 style="margin: 0px;">{{ _("CANCELLED") }}</h4></div>
    {%- endif -%}
    {% if max_pages > 1 %}
    <p class="text-right">{{ _("Page #{0} of {1}").format(page_num, max_pages) }}</p>
    {% endif %}
{%- endmacro -%}

<style>
    .table-hover>tbody>tr:hover>td, .table-hover>tbody>tr:hover>th {
    background-color: #B7CCF2;
    color:#000000;
    font-size:16px;
    }
    .print-format table, .print-format tr, 
    .print-format td, .print-format div, .print-format p {
        font-family: sans-serif;
        line-height: 90%;
        vertical-align: middle;
    }
    @media screen {
        .print-format {
            width: 210mm;
            padding: 10mm;
            min-height: 297mm;
            footer {page-break-after: always;}
        }
    }
</style>

<small>
{{ add_header(0,1,doc,letter_head, no_letterhead) }}


<table class = "table table-condensed table-bordered">
  <tr>

    <td style="width: 60%;">
        <b>{{ _("Shipping Address") }}:</b> <br> {{ doc.shipping_address }}
    </td>
    <td style="width: 40%;">

         <b>{{ _("SF-Number") }}:</b> {{ doc.name }}<br>
        <b>{{ _("Date") }}:</b> {{ doc.get_formatted("posting_date") }}<br>
    </td>
  </tr>
  <tr>
     <td style="width: 60%;">
        <b>{{ _("ATTN") }}:</b><br>{{ doc.contact_display }}<br>

    </td>
    <td style="width: 40%;">
        <b>{{ _("SHIPPED VIA") }}:</b> 
    </td>

  </tr>
  <tr>
     <td style="width: 60%;">
        <b>{{ _("PHONE") }}:</b><br>{{ doc.contact_mobile }}<br> 
    </td>
    <td style="width: 40%;">
         <b>{{ _("TRACKING#") }}:</b> 
    </td>

  </tr>
  <tr>
     <td style="width: 60%;">

    </td>
    <td style="width: 40%;">
        <b>{{ _("Sales REP") }}:</b><br>
    </td>

  </tr>
</table>

<table class="table table-condensed table-hover table-bordered">
        <tr>
            <th>Sr</th>
            <th class="text-right">Qty</th>
            <th>Description</th>
        </tr>
        {%- for row in doc.delivery_note_details -%}
        <tr>
            <td style="width: 3%;">{{ row.idx }}</td>
            <td style="width: 10%; text-align: right;">{{ row.qty }} {{ row.uom or row.stock_uom }}</td>
            <td style="width: 57%;"> {{ row.item_name }}</td>
        </tr>
        {%- endfor -%}
    </tbody>
</table>


<hr>
SAMPLES FOR LAB ANALYSIS ONLY:
</small>
